<style>
    .sign-in-page {
        display: flex;
        height: 100vh;
    }
    .sign-in-section{
        flex-grow: 1;
        background-color: #FFF;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    .sign-in-form {
        width: 60%;
        text-align: center;
    }
    .welcome-back-msg {
        color: var(--text, #130E01);
        /* UI/T2-black */
        font-family: 'Inter';
        font-size: 36px;
        font-style: normal;
        font-weight: 900;
        line-height: normal;
        margin-bottom: 10px;
    }
    .sub-msg {
        color: var(--text-secondary, #7F7F7F);
        font-family: 'Inter';
        font-size: 18px;
        font-style: normal;
        font-weight: 700;
        line-height: normal;
        margin-bottom: 45px;
    }
    .login-input {
        border-radius: 8px;
        border: 2px solid var(--element-secondary, #CDCDCD);
        display: flex;
        padding: 5px;
        margin-bottom: 15px;
    }
    .logo {
        border-right: 2px solid #CDCDCD;
        padding: 10px;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    .input {
        padding: 10px;
        text-align: left;
    }
    input[type="text"], input[type="password"] {
        outline: none;
        border: none;
        color: var(--text, #130E01);
        font-family: 'Inter';
        font-size: 18px;
        font-style: normal;
        font-weight: 700;
        line-height: normal;
    }
    input[type="button"] {
        background-color: #0075FF;
        width: 100%;
        height: 75px;
        border-radius: 8px;
        border: none;
        color: white;
        color: var(--background, #FFFAEB);
        margin-top: 20px;

        /* UI/B5-black */
        font-family: 'Inter';
        font-size: 20px;
        font-style: normal;
        font-weight: 900;
        line-height: normal;
    }
    label {
        display: block;
        color: var(--text-secondary, #7F7F7F);
        font-family: 'Inter';
        font-size: 14px;
        font-weight: 700;
        margin-bottom: 5px;
    }
    .svg-illustration{
        background-color: #0075FF;
        flex-grow: 1;
        display: flex;
        justify-content: center;
        align-items: center;
    }
</style>